import axios from "axios";
import { render } from "nunjucks";
import React, { useEffect, useState } from "react";

const SetSelect = () => {
    const [data, setData] = useState();
    const [pack, setPack] = useState();
    const handleChange = (e) => {
        console.log(e.target.value)
    }

    useEffect(() => {
        axios.get("https://api.scryfall.com/sets")
        .then(res => 
            setData(res.data.data.map(set => 
                <option key={set.id}>{set.name}</option>))
        )      
    }, []);

    //useEffect(() => {
    //    axios.get(`http://api.scryfall.com/sets/${data.set.id}`)
    //    .then(res => {
    //        setPack(res.data.uri)
    //    })
    //}, []);

    return(
    <div>
        <form type="select">
            <select value={data} onChange={handleChange}>
                {data}
            </select>
        <br></br>
            <button>Draft a pack!</button>
        </form>
        {pack}
    </div>
    )
}

export default SetSelect
