import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CardGallery = () => {
  const [selectedSet, setSelectedSet] = useState('');
  const [sets, setSets] = useState([]);
  const [cards, setCards] = useState([]);
  const [cardSize, setCardSize] = useState('small'); // Added state for card size

  useEffect(() => {
    const fetchSets = async () => {
      try {
        const response = await axios.get('https://api.scryfall.com/sets');
        setSets(response.data.data);
      } catch (error) {
        console.error('Error fetching sets:', error);
      }
    };
    fetchSets();
  }, []);

  const handleSetChange = (event) => {
    setSelectedSet(event.target.value);
  };

  const handleButtonClick = async () => {
    try {
      const response = await axios.get(`https://api.scryfall.com/cards/search?q=set%3A${selectedSet}`);
      setCards(response.data.data);
    } catch (error) {
      console.error('Error fetching cards:', error);
    }
  };

  const handleCardSizeChange = (event) => {
    setCardSize(event.target.value);
  };

  const handleOpenPack = () => {
    const selectedSet = selectedSetRef.current.value;
    const cardSize = selectedCardSize;
    if (selectedSet) {
      fetch(`https://api.scryfall.com/cards/random?q=set:${selectedSet}`)
        .then(response => response.json())
        .then(data => {
          cardGalleryRef.current.innerHTML = '';
          const cardElement = document.createElement('div');
          cardElement.className = 'card';
          const cardImage = document.createElement('img');
          cardImage.src = data.image_uris[cardSize];
          cardImage.alt = data.name;
          cardElement.appendChild(cardImage);
          cardGalleryRef.current.appendChild(cardElement);
        })
        .catch(error => console.error('Error fetching random card:', error));
    }
  };

  return (
    <div>
      <h1>Card Gallery</h1>
      <select value={selectedSet} onChange={handleSetChange}>
        <option value="">Select a Set</option>
        {sets.map(set => (
          <option key={set.code} value={set.code}>
            {set.name}
          </option>
        ))}
      </select>
      <select value={cardSize} onChange={handleCardSizeChange}> {/* Added card size selector */}
        <option value="small">Small</option>
        <option value="normal">Normal</option>
        <option value="large">Large</option>
      </select>
      <button onClick={handleButtonClick}>Generate Cards</button>
      {/* Open Pack button */}
<button onClick={handleOpenPack}>Open Pack</button>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gridGap: '1rem',
        }}
      >
        {cards.map(card => (
          <div key={card.id} style={{ padding: '0.5rem' }}>
            <img
              src={card.image_uris[cardSize]} // Dynamically set the card size
              alt={card.name}
              style={{ width: '100%', height: 'auto' }} // Set width to 100% and height to auto for responsiveness
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default CardGallery;
