import axios from "axios";
import { render } from "nunjucks";
import React, { useEffect, useState } from "react";

const SetSelect = () => {
    const [data, setData] = useState(null);
    const [pack, setPack] = useState()

    useEffect(() => {
        axios.get("https://api.scryfall.com/sets")
        .then(res => {
            setData(res.data.data.map(set => 
                <option key={set.id}>{set.name}</option>))
        })      
    }, []);

   const openPack = () => {
        setPack.map(cards =>
            <img src={data.cards.scryfall.uri}></img>)
    }

    return(
    <div>
        <form type="select">
            <select>
                {data}
            </select>
        <br></br>
            <button onSubmit={openPack}>Draft a pack!</button>
        </form>
        {pack}
    </div>
    )
}

export default SetSelect
