import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const CardGallery = () => {
  const [selectedSet, setSelectedSet] = useState('');
  const [sets, setSets] = useState([]);
  const [cards, setCards] = useState([]);
  const [cardSize, setCardSize] = useState('small'); // Added state for card size
  const [cardImages, setCardImages] = useState([])
  const setSelectRef = useRef(null)
  const cardSizeSelectRef = useRef(null)

  useEffect(() => {
    const fetchSets = async () => {
      try {
        const response = await axios.get('https://api.scryfall.com/sets');
        setSets(response.data.data);
      } catch (error) {
        console.error('Error fetching sets:', error);
      }
    };
    fetchSets();
  }, []);

  const handleSetChange = (event) => {
    setSelectedSet(event.target.value);
  };

  const handleButtonClick = async () => {
    try {
      const response = await axios.get(`https://api.scryfall.com/cards/search?q=set%3A${selectedSet}`);
      setCards(response.data.data);
    } catch (error) {
      console.error('Error fetching cards:', error);
    }
  };

  const handleCardSizeChange = (event) => {
    setCardSize(event.target.value);
  };

  const handleOpenPack = async () => {
    const selectedSet = setSelectRef.current.value;
    const cardSize = cardSizeSelectRef.current.value;

    try {
      // Fetch all cards from the chosen set
      const response = await fetch(`https://api.scryfall.com/cards/search?q=set:${selectedSet}`);
      const data = await response.json();

      // Filter out any cards that are not part of a normal booster pack
      const boosterCards = data.data.filter(card => card.booster);

      // Sort the booster cards array by rarity, assuming rarity is represented by 'rarity' property
      const sortedCards = boosterCards.sort((a, b) => {
        const rarityOrder = { common: 1, uncommon: 2, rare: 3, mythic: 4 };
        return rarityOrder[a.rarity] - rarityOrder[b.rarity];
      });

      // Select the first N cards from the sorted array, where N is the number of cards in a booster pack
      const packSize = 15; // Assume a booster pack contains 15 cards
      const selectedCards = sortedCards.slice(0, packSize);

      // Update the state to trigger a re-render with the new card images
      setCardImages(selectedCards.map(card => card.image_uris[cardSize]));
    } catch (error) {
      console.error('Error fetching booster pack:', error);
    }
  };

  return (
    <div>
      <h1>Card Gallery</h1>
      <select value={selectedSet} onChange={handleSetChange}>
        <option value="">Select a Set</option>
        {sets.map(set => (
          <option key={set.code} value={set.code}>
            {set.name}
          </option>
        ))}
      </select>
      <select value={cardSize} onChange={handleCardSizeChange}> {/* Added card size selector */}
        <option value="small">Small</option>
        <option value="normal">Normal</option>
        <option value="large">Large</option>
      </select>
      <button onClick={handleButtonClick}>View All Cards</button>
      <button onClick={handleOpenPack}>Try Opening a Pack!</button>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gridGap: '1rem',
        }}
      >
        {cards.map(card => (
          <div key={card.id} style={{ padding: '0.5rem' }}>
            <img
              src={card.image_uris.normal} // Dynamically set the card size
              alt={card.name}
              style={{ width: '100%', height: 'auto' }} // Set width to 100% and height to auto for responsiveness
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default CardGallery;
