import axios from "axios";
import { render } from "nunjucks";
import React, { useEffect, useState } from "react";

const SetSelect = () => {
    const [data, setData] = useState();
    const [pack, setPack] = useState();

    useEffect(() => {
        axios.get("https://api.scryfall.com/sets")
        .then(res => {
            setData(res.data.data.map(set => 
                <option key={set.id}>{set.name}</option>))
        })      
    }, []);


    useEffect(() => {
        axios.get(`https://api.scryfall.com/sets/aer`)
        .then(res => {
            setPack(res.icon_svg_uri)
        })
    }, [data]);


    return(
    <div>
        <form type="select">
            <select value={data}>
                {data}
            </select>
        <br></br>
            <button>Draft a pack!</button>
        </form>
        {pack}
    </div>
    )
}

export default SetSelect
