import axios from "axios";
import { render } from "nunjucks";
import React, { useEffect, useState } from "react";

const SetSelect = () => {
    const [data, setData] = useState();
    const [pack, setPack] = useState([]);

    const handleSubmit =  async () => {
        try {
            const response = await axios.get(`https://api.scryfall.com/sets/${set.id}`);
            setPack(response.data.data);
        } catch (err) {
            console.error("Error fetching set", err)
        }
    };

    useEffect(() => {
        axios.get("https://api.scryfall.com/sets")
        .then(res => 
            setData(res.data.data.map(set => 
                <option key={set.id} code={set.code}>{set.name}</option>))
        )      
    }, []);

    return(
    <div>
        <form type="select">
            <select>
                {data}
            </select>
        <br></br>
            <button onClick={handleSubmit}>Draft a pack!</button>
        </form>
        {pack.map(card => (
        <div key={card.id}>
          <img src={card.search_uri} />
        </div>
      ))}
    </div>
    )
}

export default SetSelect
