import axios from "axios";
import { render } from "nunjucks";
import React, { useEffect, useState } from "react";

const SetSelect = () => {
    const [data, setData] = useState();
    const [pack, setPack] = useState();

    const handleSubmit = () => {
        axios.get(`https://api.scryfall.com/${data.code}`)
        .then(res =>
            setPack(res.data))
    }

    useEffect(() => {
        axios.get("https://api.scryfall.com/sets")
        .then(res => 
            setData(res.data.data.map(set => 
                <option key={set.id} code={set.code}>{set.name}</option>))
        )      
    }, []);

    return(
    <div>
        <form type="select">
            <select>
                {data}
            </select>
        <br></br>
            <button onClick={{handleSubmit}}>Draft a pack!</button>
        </form>
        {pack}
    </div>
    )
}

export default SetSelect
