import axios from "axios";
import { render } from "nunjucks";
import React, { useEffect, useState } from "react";

const SetSelect = () => {
    const [data, setData] = useState(null);

    useEffect(() => {
        axios.get("https://api.scryfall.com/sets")
        .then(res => {
            setData(res.data.data.map(set => 
                <option key={set.id}>{set.name}</option>))
        })      
    }, []);

   const createPack = () => {
        setData.map(cards =>
            <img src={cards.scryfall.uri}></img>)
    }

    return(
    <form type="select">
        <select>
            {data}
        </select>
        <button onSubmit={createPack}>Draft a pack!</button>
    </form>
    )
}

export default SetSelect
