import {useState, useEffect} from "react"
import axios from "axios"

const CardViewer = () => {
  const [selectedSet, setSelectedSet] = useState('');
  const [sets, setSets] = useState([])
  const [cards, setCards] = useState([])
  const [rares, setRares] = useState([])

  useEffect(() => {
    const fetchSets = async () => {
      try {
        const response = await axios.get("https://api.scryfall.com/sets");
        setSets(response.data.data);
      } catch (error) {
        console.error("Error fetching sets:", error);
      }
    };
    fetchSets();
  }, []);

  const handleSetChange = (e) => {
    setSelectedSet(e.target.value)
  };

  const handleButtonClick = async () => {
    try {
      const response = await axios.get(`https://api.scryfall.com/cards/search?q=set%3A${selectedSet}`)
      setCards(response.data.data);
      debugger
    } catch (error) {
      console.error("Error fetching cards:", error);
    }
  };

  const handleRares = () => {

    setRares(cards.data.data[0].rarity)
  }




  return (
    <div>
      <h1>Card Viewer</h1>
      <select value={selectedSet} onChange={handleSetChange}>
        <option value="">Select a Set</option>
        {sets.map(set => (
          <option key={set.code} value={set.code}>
            {set.name}
          </option>
        ))}
      </select>
      <button onClick={handleButtonClick}>View Cards!</button>
      <div
      style={{
        display: "grid",
        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 0.5fr))',
      }}>
      <button onClick={handleRares}></button>
        {cards.map(card => (
          <div key={card.id} style={{ padding: '0.5rem'}}>
            <img
            src={card.image_uris.normal}
            alt={card.name}
            style={{width: "100%", height: "auto"}}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default CardViewer
