import {useState, useEffect} from "react"
import axios from "axios"

const CardViewer = () => {
  const [selectedSet, setSelectedSet] = useState('');
  const [sets, setSets] = useState([])
  const [cards, setCards] = useState([])
  const [pack, setPack] = useState([])
  const [rares, setRares] = useState([])
  const [uncommons, setUncommons] = useState([])
  const [commons, setCommons] = useState([])

  useEffect(() => {
    const fetchSets = async () => {
      try {
        const response = await axios.get("https://api.scryfall.com/sets");
        // debugger
        // setSets(response.data.data);
        setSets(response.data.data.filter(set => set.name == "Phyrexia: All Will Be One"));
      } catch (error) {
        console.error("Error fetching sets:", error);
      }
    };
    fetchSets();
  }, []);

  const handleSetChange = async (e) => {
    setSelectedSet(e.target.value)
    try {
      const response = await axios.get(`https://api.scryfall.com/cards/search?q=set%3A${e.target.value}`)
      const cards = response.data.data
      setCards(cards);
      setRares(cards.filter(card => card.rarity == "rare"))
      setUncommons(cards.filter(card => card.rarity == "uncommon"))
      setCommons(cards.filter(card => card.rarity == "common"))
    } catch (error) {
      console.error("Error fetching cards:", error);
    }
  };

  // const handleButtonClick = async () => {
  //   try {
  //     const response = await axios.get(`https://api.scryfall.com/cards/search?q=set%3A${selectedSet}`)
  //     setCards(response.data.data);
  //   } catch (error) {
  //     console.error("Error fetching cards:", error);
  //   }
  // };

  const generatePack = () => {
    let pack = []
    // let rares = cards.filter(card => card.rarity == "rare")
    let raresCopy = rares
    let uncommonsCopy = [ ...uncommons ]
    uncommonsCopy.sort(() => 0.5 - Math.random());
    debugger
    let commonsCopy = commons
    // let uncommons = cards.filter(card => card.rarity == "uncommon")
    // let commons = cards.filter(card => card.rarity == "common")

    let randomRare = (Math.floor(Math.random() * (raresCopy.length)))
    let randomUncommons = uncommonsCopy.slice(0,3)
    let randomCommons = (Math.floor(Math.random() * (commonsCopy.length)))
    pack.push(raresCopy[randomRare])
    pack.push(uncommonsCopy[randomUncommons])
    setPack(pack)
   debugger
    }

  const handleUncommons = () => {
    setUncommons(cards.filter(card => card.rarity == "uncommon"))
    }

  const handleCommons = () => {
    setCommons(cards.filter(card => card.rarity == "common"))
  }

  return (
    <div>
      <h1>Card Viewer</h1>
      <select value={selectedSet} onChange={handleSetChange}>
        <option value="">Select a Set</option>
        {sets.map(set => (
          <option key={set.code} value={set.code}>
            {set.name}
          </option>
        ))}
      </select>
      {/* <button onClick={handleButtonClick}>View Cards!</button> */}
      <div
      style={{
        display: "grid",
        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 0.5fr))',
      }}>
      <button onClick={generatePack}>Open a Pack</button>
        {pack.map(card => (
            <div key={card.id} style={{ padding: '0.5rem'}}>
              <img
              src={card.image_uris.normal}
              alt={card.name}
              style={{width: "100%", height: "auto"}}
              />
            </div>
          ))}
      {/* <button onClick={handleUncommons}>Show me the Uncommons</button> */}
        {/* {uncommons.map(card => (
            <div key={card.id} style={{ padding: '0.5rem'}}>
              <img
              src={card.image_uris.normal}
              alt={card.name}
              style={{width: "100%", height: "auto"}}
              />
            </div>
          ))}  */}
       {/* <button onClick={handleCommons}>Show me the Commons</button> */}
        {/* {commons.map(card => (
            <div key={card.id} style={{ padding: '0.5rem'}}>
              <img
              src={card.image_uris.normal}
              alt={card.name}
              style={{width: "100%", height: "auto"}}
              />
            </div>
          ))} */}
        {/* {cards.map(card => (
          <div key={card.id} style={{ padding: '0.5rem'}}>
            <img
            src={card.image_uris.normal}
            alt={card.name}
            style={{width: "100%", height: "auto"}}
            />
          </div>
        ))} */}
      </div>
    </div>
  );
};

export default CardViewer
